mapper.select:
SELECT *
FROM articles
WHERE article_id = #{ articleId }

mapper.update:
UPDATE articles


        article_title = #{et.articleTitle},


        article_content = #{et.articleContent},


        article_view = #{et.articleView},


        article_date = #{et.articleDate},


        article_like_count = #{et.articleLikeCount},


        article_show = #{et.articleShow}


        article_show = #{et.articleImg}


WHERE article_id = #{et.articleId}
